function loadImages(k) {
    var k = $.extend({
        isShowLoading: true,
        progress: function (c) {
            j.html(c + "%")
        }, finish: function () {
            //$("#container").removeClass("hide")


                $('#homepage').fadeIn();

                setTimeout(function () {
                    $("#loading").fadeOut(300);
                    //alert("加载完毕");
                }, 1300);
        }, complete: function () {
        },
        //cdnPath: "http://image.pgchina.birthday.i2mago.com.cn/"
        cdnPath: "./"
    }, k), g = k.images,
        j = $("#loader"),  // 加载进度
        a = g.length,
        d = a,
        b = 0,
        f = [],
        e = {};
    if (a < 1) {
        j.removeClass("loading");
        k.finish.call(f);
        k.complete.call(f);
        return
    }
    if (k.isShowLoading) {
        j.html("0%").addClass("loading")
    }
    var h = function () {
        b++;
        k.progress.call(f[b], Math.floor(b / a * 100));
        if (b >= a) {
            setTimeout(function () {
                j.removeClass("loading");
                k.finish.call(f);
                k.complete.call(f)
            }, 50)
        }
    };
    while (d--) {
        e = new Image();
        e.src = k.cdnPath + g[d];
        if (e.complete) {
            h()
        } else {
            e.onload = e.onerror = function () {
                h()
            }
        }
        f[d] = e
    }
};