CREATE TABLE IF NOT EXISTS `ims_mon_magazine` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) NOT NULL, `weid` int(11) NOT NULL, `title` varchar(200) NOT NULL, `cover_img` varchar(500) NOT NULL, `zx_tel` varchar(50) DEFAULT NULL, `btn_icons` varchar(500) DEFAULT NULL, `btn1_name` varchar(500) DEFAULT NULL, `btn1_url` varchar(500) DEFAULT NULL, `btn2_name` varchar(500) DEFAULT NULL, `btn2_url` varchar(500) DEFAULT NULL, `new_title` varchar(200) DEFAULT NULL, `new_icon` varchar(200) DEFAULT NULL, `new_content` varchar(200) DEFAULT NULL, `share_title` varchar(200) DEFAULT NULL, `share_icon` varchar(200) DEFAULT NULL, `share_content` varchar(200) DEFAULT NULL, `share_Layer` varchar(500) DEFAULT NULL, `createtime` int(10) DEFAULT NULL, `updatetime` int(10) DEFAULT NULL, `view_count` int(10) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_mon_magazine_page` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `mid` int(10) NOT NULL, `page` int(3) DEFAULT NULL, `pname` varchar(50) DEFAULT NULL, `purl` varchar(500) DEFAULT NULL, `createtime` int(10) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
