manifest.install:
DROP TABLE IF EXISTS ims_fwei_forms; CREATE TABLE `ims_fwei_forms` ( `formid` int(10) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(10) unsigned NOT NULL DEFAULT '0', `rid` int(10) unsigned NOT NULL DEFAULT '0', `title` varchar(100) NOT NULL, `thumb` varchar(100) NOT NULL, `description` varchar(100) NOT NULL, `content` text NOT NULL, `info` varchar(100) NOT NULL, `num` int(10) NOT NULL DEFAULT '0', `stime` int(10) unsigned NOT NULL DEFAULT '0', `etime` int(10) unsigned NOT NULL DEFAULT '0', `total` int(10) unsigned NOT NULL DEFAULT '0', `show_desc` tinyint(1) unsigned NOT NULL DEFAULT '0', `timeline` int(10) unsigned NOT NULL DEFAULT '0', `credit` int(10) unsigned NOT NULL DEFAULT '0', `coupon` int(10) unsigned NOT NULL DEFAULT '0', PRIMARY KEY (`formid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS ims_fwei_forms_attrs; CREATE TABLE `ims_fwei_forms_attrs` ( `attr_id` int(10) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(10) unsigned NOT NULL DEFAULT '0', `rid` int(10) unsigned NOT NULL DEFAULT '0', `formid` int(10) unsigned NOT NULL DEFAULT '0', `title` varchar(100) NOT NULL, `description` varchar(100) NOT NULL, `type` varchar(10) NOT NULL, `extra` varchar(500) NOT NULL, `is_must` tinyint(1) unsigned NOT NULL DEFAULT '1', `is_show` tinyint(1) unsigned NOT NULL DEFAULT '0', `rule` varchar(100) NOT NULL, `defvalue` varchar(100) NOT NULL, `sort` smallint(5) unsigned NOT NULL DEFAULT '0', PRIMARY KEY (`attr_id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS ims_fwei_forms_fans; CREATE TABLE `ims_fwei_forms_fans` ( `fid` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) unsigned NOT NULL DEFAULT '0', `formid` int(10) unsigned NOT NULL DEFAULT '0', `uid` int(10) unsigned NOT NULL DEFAULT '0', `openid` varchar(50) NOT NULL, `status` tinyint(1) unsigned NOT NULL DEFAULT '0', `created` int(10) unsigned NOT NULL DEFAULT '0', `updated` int(10) unsigned NOT NULL DEFAULT '0', PRIMARY KEY (`fid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS ims_fwei_forms_values; CREATE TABLE `ims_fwei_forms_values` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) unsigned NOT NULL DEFAULT '0', `formid` int(10) unsigned NOT NULL DEFAULT '0', `fid` int(10) unsigned NOT NULL DEFAULT '0', `attr_id` int(10) unsigned NOT NULL DEFAULT '0', `attr_value` text NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
