application.description:
发送@可以找到随机异性
@编号可以找到直接对话某个人
搜索+关键字可以找到人

manifest.install:
DROP TABLE IF EXISTS ims_yobydashan_friend; CREATE TABLE `ims_yobydashan_friend` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) DEFAULT NULL, `fromuser` varchar(60) DEFAULT NULL COMMENT '我的id', `wid` varchar(10) DEFAULT NULL COMMENT '朋友id', `yname` varchar(20) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS ims_yobydashan_sms; CREATE TABLE `ims_yobydashan_sms` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `content` varchar(144) DEFAULT NULL COMMENT '字数', `createtime` int(10) DEFAULT NULL, `fromuser` varchar(64) DEFAULT NULL, `touser` varchar(64) DEFAULT NULL, `isread` tinyint(1) DEFAULT '0', `weid` int(10) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS ims_yobydashan_user; CREATE TABLE `ims_yobydashan_user` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) DEFAULT NULL COMMENT '微信编号', `wid` varchar(10) DEFAULT NULL COMMENT '聊天编号', `sex` tinyint(1) DEFAULT '0', `yname` varchar(30) DEFAULT NULL COMMENT '姓名', `xi` varchar(100) DEFAULT NULL COMMENT '年级与系别', `fromuser` varchar(64) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
