DROP TABLE IF EXISTS ims_hchighguess_images; CREATE TABLE `ims_hchighguess_images` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned NOT NULL, `wid` int(10) unsigned NOT NULL COMMENT '词条ID', `rid` int(10) unsigned NOT NULL, `mid` int(10) unsigned NOT NULL COMMENT '会员ID', `image` varchar(255) DEFAULT NULL, `createtime` int(10) unsigned NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS ims_hchighguess_member; CREATE TABLE `ims_hchighguess_member` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned NOT NULL, `rid` int(10) unsigned NOT NULL, `from_user` varchar(50) DEFAULT NULL, `mobile` varchar(11) NOT NULL DEFAULT '' COMMENT '手机号码', `avatar` varchar(255) DEFAULT NULL, `realname` varchar(50) NOT NULL DEFAULT '', `createtime` int(10) unsigned NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS ims_hchighguess_reply; CREATE TABLE `ims_hchighguess_reply` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(11) DEFAULT NULL, `title` varchar(255) DEFAULT NULL, `cover` varchar(255) DEFAULT NULL, `description` text, `sharetitle` varchar(255) DEFAULT NULL, `sharecover` varchar(255) DEFAULT NULL, `sharedescription` text, `gzurl` varchar(255) DEFAULT NULL, `level` tinyint(1) NOT NULL DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS ims_hchighguess_selectlog; CREATE TABLE `ims_hchighguess_selectlog` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned NOT NULL, `wid` int(10) unsigned NOT NULL, `imgid` int(10) unsigned NOT NULL, `from_user` varchar(50) DEFAULT NULL, `realname` varchar(50) NOT NULL DEFAULT '', `image` varchar(255) DEFAULT NULL, `word` varchar(20) DEFAULT NULL, `createtime` int(10) unsigned NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
DROP TABLE IF EXISTS ims_hchighguess_words; CREATE TABLE `ims_hchighguess_words` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned NOT NULL, `word` varchar(20) DEFAULT NULL, `words` varchar(100) DEFAULT NULL, `createtime` int(10) unsigned NOT NULL, `isopen` tinyint(1) unsigned NOT NULL DEFAULT '1', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
