CREATE TABLE IF NOT EXISTS `ims_video_reply` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) unsigned NOT NULL, `title` varchar(50) NOT NULL, `description` varchar(255) NOT NULL, `mediaid` varchar(255) NOT NULL, `createtime` int(10) NOT NULL, PRIMARY KEY (`id`), KEY `rid` (`rid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
