CREATE TABLE IF NOT EXISTS `ims_j_money_award` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned NOT NULL, `gid` int(10) unsigned NOT NULL COMMENT '活动ID', `level` varchar(255) DEFAULT NULL COMMENT '奖品等级', `thumb` varchar(255) DEFAULT NULL COMMENT '奖品图片', `prizetype` tinyint(1) NOT NULL DEFAULT '0' COMMENT '奖品类型，0实物，1红包，2卡券', `leavel` int(10) NOT NULL COMMENT '奖品级别', `title` varchar(50) NOT NULL COMMENT '奖品名称', `isprize` tinyint(1) NOT NULL DEFAULT '0', `renum` int(10) NOT NULL DEFAULT '0' COMMENT '数量', `total` int(10) NOT NULL DEFAULT '0' COMMENT '数量', `probalilty` varchar(5) NOT NULL COMMENT '概率单位%', `deg` int(5) NOT NULL COMMENT '角度', `description` varchar(100) NOT NULL DEFAULT '' COMMENT '描述', `credit` int(11) NOT NULL COMMENT '奖励积分', PRIMARY KEY (`id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC AUTO_INCREMENT=7 ;
