CREATE TABLE IF NOT EXISTS `ims_mon_exchange_admin` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(11) NOT NULL, `gid` int(10) DEFAULT NULL, `admin` varchar(1000) DEFAULT NULL, `pwd` varchar(500) DEFAULT NULL, `token` varchar(100) DEFAULT NULL, `remark` varchar(1000) DEFAULT NULL, `createtime` int(10) DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_mon_exchange_game` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(11) NOT NULL, `gname` varchar(100) NOT NULL, `gcode` varchar(2000) DEFAULT NULL, `createtime` int(10) DEFAULT '0', PRIMARY KEY (`id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_mon_exchange_record` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) NOT NULL, `gid` int(10) NOT NULL, `aid` int(10) NOT NULL, `uname` varchar(100) DEFAULT NULL, `unickname` varchar(1000) DEFAULT NULL, `utel` varchar(100) DEFAULT NULL, `pname` varchar(1000) DEFAULT NULL, `remark` text, `createtime` int(10) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC AUTO_INCREMENT=1 ;
